function createInfoBox(title) {

    var infoBox =
        '<div id="src_profile_customization" class="profile_customization">'
            + '    <div id="profile_customization_header"  class="profile_customization_header ellipsis">'
            + '    ' + title
            + '    </div>'
            + '    <div class="profile_customization_block">'
            + '        <div class="customtext_showcase">'
            + '            <div class="showcase_content_bg showcase_notes">'
            + '                <div id="steamrep_checker">'
            + '                </div>'
            + '            </div>'
            + '        </div>'
            + '    </div>'
            + '</div>';

    // Adding the profile info box
    if ($('.profile_customization_area').length > 0) {
        $('.profile_customization_area').prepend(infoBox)
    }
    else {
        $('.profile_leftcol')
            .prepend('<div class="profile_customization_area"></div>')
            .prepend(infoBox);
    }
}

function createScammerWarningDialog() {
    var dialog =
        '<div id="dialog-message" title="WARNING: SCAMMER">'
            + '<p>' + scammerbig + ' This user has been marked as a scammer on SteamRep.com. </p>'
            + '<p>To protect yourself and prevent thieves from profiting, <b style="color: red;">do not trade with this person</b>.'
            + 'Players shouldn\'t be encouraged to steal. Supporting them can hurt your reputation.</p>'
            + '</div>';
    $('body').prepend(dialog);
    $("#dialog-message").dialog({
        modal: true,
        draggable: false,
        height: 285,
        width: 560,
        buttons: {
            Ok: function () {
                $(this).dialog("close");
            }
        }
    });
}

function tagUser(tagType, reputation) {
    switch (tagType) {
        case 'scammer':
            $('#src_rep').html(reputation);
            $('#src_rep').addClass('scammer');

            $('.playerAvatar.profile_header_size').css('background', 'red');
            $('div.profile_header_summary > div.persona_name').addClass('scammer');
            $('div.profile_header_summary > div.persona_name').prepend(scammer);

            break;
        case 'caution':
            $('#src_rep').text(reputation);
            $('#src_rep').addClass('caution');

            $('.playerAvatar.profile_header_size').css('background', 'orange');
            $('div.profile_header_summary > div.persona_name').addClass('caution');
            $('div.profile_header_summary > div.persona_name').prepend(caution);

            break;
        case 'trusted':
            $('#src_rep').text(reputation);
            $('#src_rep').addClass('trusted');

            $('.playerAvatar.profile_header_size').css('background', 'lime');
            $('div.profile_header_summary > div.persona_name').addClass('trusted');
            $('div.profile_header_summary > div.persona_name').prepend(trusted);

            break;
        default:
            $('#src_rep').text('No special rep (there might be pending reports against this user)');
			break;
    }
}

function handleRep(reputation) {
    if (reputation == '') {
        tagUser('', reputation);
    }
    else if (reputation.search(/(banned|scammer)/i) > -1) {
        tagUser('scammer', reputation);
        createScammerWarningDialog();
    }
    else if (reputation.search(/(admin|middleman|valve employee|trusted)/i) > -1) {
        tagUser('trusted', reputation);

    }
    else if (reputation.search(/caution/i) > -1) {
        tagUser('caution', reputation)
    }
}

function querySteamRep(sid64) {
    var sr_api_url = 'https://steamrep.com/api/beta/reputation/' + sid64 + '?json=1&source=sr-check';

    $.ajax({
        url: sr_api_url,
        dataType: 'json'
    })
        .done(function (xml, textStatus, jqxhr) {
            srResponse = jqxhr.responseJSON;

            $('#src_rep').attr('title', srResponse.steamrep.reputation);
            handleRep(srResponse.steamrep.reputation);

        })
        .fail(function (e) {
            $('#src_rep').text('Error communicating with SteamRep.com. Click here to visit the website.');
        });
}

function getReputation() {
    var url = document.location.origin + document.location.pathname + '/?xml=1';

    $.ajax({
        url: url,
        dataType: 'xml'
    })
        .done(function (xml) {
            var sid64 = $(xml).find("steamID64").text();

            $('#src_rep').attr('href', 'http://steamrep.com/profiles/' + sid64);

            var privacy = $(xml).find("privacyState").text();
            switch (privacy){
                case 'public':
                    $('#steamrep_checker').append('<p><b>Privacy : </b>Public</p>');
                    break;
                case 'friendsonly':
                    $('#steamrep_checker').append('<p><b>Privacy : </b>Friends Only</p>');
                    break;
                case 'usersonly':
                    $('#steamrep_checker').append('<p><b>Privacy : </b>Users Only</p>');
                    break;
                case 'private':
                    $('#steamrep_checker').append('<p><b>Privacy : </b>Private</p>');
                    break;
            }
            $('#steamrep_checker').append('<p><b>Permalink : </b><a id="src_profile_permalink" href="http://steamcommunity.com/profiles/' + sid64 + '">http://steamcommunity.com/profiles/' + sid64 + '</a></p>');
            $('#steamrep_checker').append('<p><b>Pending reports : </b><a id="src_pending_reports" href="http://forums.steamrep.com/search/search/?keywords=' + sid64 + '&o=date">Search SteamRep Forum</a></p>');
            $('#steamrep_checker').append('<p><label for="src_sid64tb">SteamID64 : </label><input id="src_sid64tb" type="text" value="' + sid64 + '" readonly /></p>');



            $('#steamrep_checker').append('<p><img class="src_icon" alt="logo" width="16" height="16" src="'+ tf2bp +'" /><a href="http://backpack.tf/profiles/' + sid64 + '">bp.tf</a> | '
                                              + '<img class="src_icon" alt="logo" width="16" height="16" src="'+ dota2bp +'" /><a href="http://dota2.backpack.tf/profiles/' + sid64 + '">dota2.bp.tf</a> | '
                                              + '<img class="src_icon" alt="logo" width="16" height="16" src="'+ dota2bp +'" /> <a href="http://www.csgovalue.com/?id=' + sid64 + '">CSGOValue.com</a> | '
                                              + '<img class="src_icon" alt="logo" width="16" height="16" src="'+ st +'" /><a href="http://www.steamtrades.com/user/id/' + sid64 + '">SteamTrades.com</a></p>');

            querySteamRep(sid64);
        })
        .fail(function (e) {
            $('#src_rep').text('Error getting the SteamID64');
        });
}

function setLoadingStatus() {
    $('#steamrep_checker').append(
        '<p><b>Reputation : </b><img  class="src_icon" alt="logo" width="16" height="16" src="'+ sr +'" /><a href="#" id="src_rep">' + loadingImage + '&nbsp; Checking SteamRep...</a></p>');
}

var re = new RegExp("steamcommunity.com/(?:id|profiles)/[a-zA-Z0-9_]+[/]{0,1}$");

if (re.exec(document.location.origin + document.location.pathname)) {
    createInfoBox('SteamRep Check report');
    setLoadingStatus();
    getReputation();
}